function sendForm(e){var t="some path to server",r=(e.form,e.name),s=e.password,n=e.submit;r.addEventListener("input",function(){r.classList.remove("error"),s.classList.remove("error")}),n.addEventListener("click",function(e){if(e.preventDefaut,r.value&&s.value){var n=new XMLHttpRequest,a=JSON.stringify({name:r.value,password:s.value});n.open("POST",t,!0),n.setRequestHeader("Content-type","application/json; charset=utf-8"),n.send(a)}else r.classList.add("error"),s.classList.add("error")})}sendForm({form:document.forms[0],name:document.querySelector(".authentification__name"),password:document.querySelector(".authentification__password"),submit:document.querySelector(".authentification__submit")});